mapper.select:
select application_form_number, applicant_name, applicant_id, applicant_country, issue_date, total_amount, customs_confirm_amount, issue_merchant_name, status
from b_application_form
where application_form_number = #{applicationFormNumber,jdbcType=VARCHAR}

mapper.update:
update b_application_form
set status = 2, customs_confirm_amount = #{customsConfirmAmount,jdbcType=DECIMAL}
where application_form_number = #{applicationFormNumber,jdbcType=VARCHAR}

update b_application_form
set status = 3
where application_form_number = #{applicationFormNumber,jdbcType=VARCHAR}
