mapper.insert:
insert into b_seller (id, merchant_id, seller_tax_id
  )
values (#{id,jdbcType=INTEGER}, #{merchantId,jdbcType=VARCHAR}, #{sellerTaxId,jdbcType=VARCHAR}
  )

mapper.update:
update b_seller
set merchant_id = #{merchantId,jdbcType=VARCHAR},
  seller_tax_id = #{sellerTaxId,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, merchant_id, seller_tax_id
from b_seller
where id = #{id,jdbcType=INTEGER}
